
var editor = {};
$(function(){

    var $3 = THREE;

    var viewport = $('div.viewport');
    var width = viewport.width();
    var height = viewport.height();

    var renderer = new $3.WebGLRenderer({ clearColor : 0x4d4d59 });
    var camera = new $3.Camera( 45, width/height, 0.01, 10000 );
    var scene = new $3.Scene();

    camera.position.z = 300;
    renderer.setSize( width, height );

    viewport.append( renderer.domElement );
    
    var sphereMat = new $3.MeshLambertMaterial({ color : 0xCC0000 });
    var sphere = new $3.Mesh( new $3.SphereGeometry( 50, 20, 20 ), sphereMat );
    scene.addChild( sphere );

    var pointLight = new $3.PointLight( 0xFFFFFF );
    pointLight.position.x = 10;
    pointLight.position.y = 50;
    pointLight.position.z = 130;
    scene.addLight( pointLight );

    var start = (new Date()).getMilliseconds();
    renderer.render( scene, camera );
    var end = (new Date()).getMilliseconds();
    console.log( 'Rendered in: '+(end-start)+' ms' );

}); // end editor on ready


